import { listMenu, getMenu, treeselect, delMenu, addMenu, updateMenu } from "@/api/system/menu";
import Treeselect from "@riophae/vue-treeselect";
import "@riophae/vue-treeselect/dist/vue-treeselect.css";
import IconSelect from "@/components/IconSelect";
export default {
  name: "Menu",
  components: {
    Treeselect: Treeselect,
    IconSelect: IconSelect
  },
  data: function data() {
    return {
      // 遮罩层
      loading: true,
      // 菜单表格树数据
      menuList: [],
      // 菜单树选项
      menuOptions: [],
      // 弹出层标题
      title: "",
      // 是否显示弹出层
      open: false,
      // 菜单状态数据字典
      visibleOptions: [],
      // 查询参数
      queryParams: {
        menuName: undefined,
        visible: undefined
      },
      // 表单参数
      form: {},
      // 表单校验
      rules: {
        menuName: [{
          required: true,
          message: "菜单名称不能为空",
          trigger: "blur"
        }],
        orderNum: [{
          required: true,
          message: "菜单顺序不能为空",
          trigger: "blur"
        }]
      }
    };
  },
  created: function created() {
    var _this = this;

    this.getList();
    this.getDicts("sys_show_hide").then(function (response) {
      _this.visibleOptions = response.data;
    });
  },
  methods: {
    // 选择图标
    selected: function selected(name) {
      this.form.icon = name;
    },

    /** 查询菜单列表 */
    getList: function getList() {
      var _this2 = this;

      this.loading = true;
      listMenu(this.queryParams).then(function (response) {
        _this2.menuList = response.data;
        _this2.loading = false;
      });
    },

    /** 查询菜单下拉树结构 */
    getTreeselect: function getTreeselect() {
      var _this3 = this;

      treeselect().then(function (response) {
        _this3.menuOptions = [];
        var menu = {
          id: 0,
          label: '主类目',
          children: []
        };
        menu.children = response.data;

        _this3.menuOptions.push(menu);
      });
    },
    // 菜单显示状态字典翻译
    visibleFormat: function visibleFormat(row, column) {
      if (row.menuType == "F") {
        return "";
      }

      return this.selectDictLabel(this.visibleOptions, row.visible);
    },
    // 取消按钮
    cancel: function cancel() {
      this.open = false;
      this.reset();
    },
    // 表单重置
    reset: function reset() {
      this.form = {
        menuId: undefined,
        parentId: 0,
        menuName: undefined,
        icon: undefined,
        menuType: "M",
        orderNum: undefined,
        isFrame: "1",
        visible: "0"
      };
      this.resetForm("form");
    },

    /** 搜索按钮操作 */
    handleQuery: function handleQuery() {
      this.getList();
    },

    /** 新增按钮操作 */
    handleAdd: function handleAdd(row) {
      this.reset();
      this.getTreeselect();

      if (row != null) {
        this.form.parentId = row.menuId;
      }

      this.open = true;
      this.title = "添加菜单";
    },

    /** 修改按钮操作 */
    handleUpdate: function handleUpdate(row) {
      var _this4 = this;

      this.reset();
      this.getTreeselect();
      getMenu(row.menuId).then(function (response) {
        _this4.form = response.data;
        _this4.open = true;
        _this4.title = "修改菜单";
      });
    },

    /** 提交按钮 */
    submitForm: function submitForm() {
      var _this5 = this;

      this.$refs["form"].validate(function (valid) {
        if (valid) {
          if (_this5.form.menuId != undefined) {
            updateMenu(_this5.form).then(function (response) {
              if (response.code === 200) {
                _this5.msgSuccess("修改成功");

                _this5.open = false;

                _this5.getList();
              } else {
                _this5.msgError(response.msg);
              }
            });
          } else {
            addMenu(_this5.form).then(function (response) {
              if (response.code === 200) {
                _this5.msgSuccess("新增成功");

                _this5.open = false;

                _this5.getList();
              } else {
                _this5.msgError(response.msg);
              }
            });
          }
        }
      });
    },

    /** 删除按钮操作 */
    handleDelete: function handleDelete(row) {
      var _this6 = this;

      this.$confirm('是否确认删除名称为"' + row.menuName + '"的数据项?', "警告", {
        confirmButtonText: "确定",
        cancelButtonText: "取消",
        type: "warning"
      }).then(function () {
        return delMenu(row.menuId);
      }).then(function () {
        _this6.getList();

        _this6.msgSuccess("删除成功");
      }).catch(function () {});
    }
  }
};
